export = PWSubscribePopup;
export as namespace PWSubscribePopup;

declare module PWSubscribePopup {
    class PWSubscribePopup {
        constructor(pw: Pushwoosh);

        /**
         * Toggle widget
         */
        public toggle(isOpened?: boolean): void;
        public initPopup(): Promise<void>
    }
}

interface Pushwoosh {
    isSubscribed: () => Promise<boolean>,
    isDeviceUnregistered: () => boolean,
    driver: {
        getPermission: () => Promise<string>;
    },
    paramsModule: {
        subscriptionPopupLastOpen: number,
        setSubscriptionPopupLastOpen: (timestamp: number) => null
    },
    subscribe: () => Promise<void>
}
